mapper.select:
select count(*) as count
from (select * from product_tb
group by eng_name)a;

select * from product_tb where kor_name LIKE #{title} OR
eng_name LIKE
#{title}
group by eng_name
limit #{begin}, #{range};

select count(*) as
count
from (
select p.*
from
product_tb as p
join category_tb as c
on p.category_id = c.id
where c.name
like #{name}
GROUP BY eng_name
) as a;

select name as brand_name ,id
from brand_tb
where id=#{id};

select * from product_tb
where brand_id=#{id}
group by
eng_name
limit #{begin},
#{range};

select count(*) as
count
from (
select *
from product_tb
where brand_id=#{id}
group by eng_name
)as a;

select count(*) as count
from (
select * from product_tb
where kor_name LIKE #{korName} OR
eng_name LIKE
#{engName}
