mapper.select:
select product_id
from order_tb
where id=#{orderId}

mapper.update:
update review_tb
set title=#{title}, content=#{content}, review_category_id=#{reviewCategoryId}, origin_file_name=#{originFileName}, upload_file_name=#{uploadFileName}
where id=#{id}

update.if:
update review_tb 
set heart=heart+1
where id=#{id}

update review_tb 
set heart=heart-1
where id=#{id}
